con:configuration.scriptText:
import groovy.json.JsonSlurper
def response = context.testCase.getTestStepByName("Login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(response)
def access = holder.accessToken
context.testCase.testSuite.setPropertyValue("token", "bearer "+access)
context.testCase.testSuite.project.setPropertyValue("token","bearer "+access)

import groovy.json.JsonSlurper
def response = context.testCase.getTestStepByName("Get all user").getPropertyValue("response")
def holder = new JsonSlurper().parseText(response)
def count = holder.count
for(def i=0;i<count;i++)
{
	if(holder.users[i].email=="balisha@gmail.com")
	{
		def id = holder.users[i]._id
		log.info id
		def id1 = id.toString()
		context.testCase.testSuite.setPropertyValue("id",id1)

	}
}

import groovy.json.JsonSlurper
def response = context.testCase.getTestStepByName("Get all product").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(response)
def count = holder.count
for(def i=0;i<count;i++)
{
	if(holder.products[i].name=="dua")
	{
		def id = holder.products[i]._id
		log.info id
		def id21 = id.toString()
		context.testCase.testSuite.setPropertyValue("idP",id21)
		context.testCase.setPropertyValue("idP",id21)
//i want to access this acroos project level
		context.testCase.testSuite.project.setPropertyValue("idP",id21)
	}
}

